import React, { useEffect, useRef, useState } from "react";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";

const loadWin95Css = () => {
  const link = document.createElement("link");
  link.rel = "stylesheet";
  link.href = process.env.PUBLIC_URL + "/assets/win95.css";
  document.head.appendChild(link);
};

// Desktop icon (unchanged)
function DesktopIcon({ id, label, img, x, y, selected, onSelect, onOpen }) {
  return (
    <div className="loaded-icon" style={{ position: "absolute", right: x, top: y, width: 75 }}>
      <div
        role="button"
        tabIndex={0}
        onClick={() => onSelect(id)}
        onDoubleClick={() => onOpen(id)}
        onKeyDown={(e) => {
          if (e.key === "Enter") onOpen(id);
          if (e.key === " ") onSelect(id);
        }}
        className={`desktop-icon ${selected ? "selected" : ""}`}
        style={{ textDecoration: "none", display: "block", textAlign: "center", cursor: "pointer" }}
      >
        <img src={img} alt={`${label} Icon`} style={{ width: 52, height: 52 }} />
        <div className="desktop-icon-title">{label}</div>
      </div>
    </div>
  );
}

/** Draggable window with Windows-like header controls */
function DraggableWindow({
  id,
  title,
  iconSrc,
  initial = { left: 60, top: 260, width: 640 },
  z,
  bringToFront,
  minimized,
  maximized,
  onMinimize,
  onMaximize,
  onClose,
  children,
}) {
  const wrapperRef = useRef(null);
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [startOffset, setStartOffset] = useState({ x: 0, y: 0 });
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const dragging = useRef(false);

  // Drag only when not maximized and not minimized
  useEffect(() => {
    const root = wrapperRef.current;
    if (!root) return;
    const handle = root.querySelector(".card-header");
    if (!handle) return;

    const onDown = (e) => {
      if (minimized || maximized) return;
      dragging.current = true;
      bringToFront(id);
      const p = e.touches?.[0] ?? e;
      setDragStart({ x: p.clientX, y: p.clientY });
      setStartOffset({ ...offset });
      document.body.style.userSelect = "none";
    };
    const onMove = (e) => {
      if (!dragging.current) return;
      const p = e.touches?.[0] ?? e;
      const dx = p.clientX - dragStart.x;
      const dy = p.clientY - dragStart.y;
      setOffset({ x: startOffset.x + dx, y: startOffset.y + dy });
    };
    const onUp = () => {
      dragging.current = false;
      document.body.style.userSelect = "";
    };

    const opts = { passive: false };
    handle.addEventListener("pointerdown", onDown, opts);
    window.addEventListener("pointermove", onMove, opts);
    window.addEventListener("pointerup", onUp, opts);

    handle.addEventListener("mousedown", onDown, opts);
    window.addEventListener("mousemove", onMove, opts);
    window.addEventListener("mouseup", onUp, opts);

    handle.addEventListener("touchstart", onDown, opts);
    window.addEventListener("touchmove", onMove, opts);
    window.addEventListener("touchend", onUp, opts);

    return () => {
      handle.removeEventListener("pointerdown", onDown, opts);
      window.removeEventListener("pointermove", onMove, opts);
      window.removeEventListener("pointerup", onUp, opts);

      handle.removeEventListener("mousedown", onDown, opts);
      window.removeEventListener("mousemove", onMove, opts);
      window.removeEventListener("mouseup", onUp, opts);

      handle.removeEventListener("touchstart", onDown, opts);
      window.removeEventListener("touchmove", onMove, opts);
      window.removeEventListener("touchend", onUp, opts);

      document.body.style.userSelect = "";
    };
  }, [offset, dragStart, startOffset, minimized, maximized, id, bringToFront]);

  if (!wrapperRef) return null;

  // Hidden if minimized
  const hidden = minimized;

  // Maximize styles
  const maxStyle = maximized
    ? {
        left: "0.5rem",
        top: "0.5rem",
        width: "calc(100% - 1rem)",
        height: "calc(100% - 4.25rem)", // leave room for taskbar
        transform: "translate(0,0)",
      }
    : {
        left: initial.left,
        top: initial.top,
        width: initial.width || "min(1100px, 96vw)",
        transform: `translate(${offset.x}px, ${offset.y}px)`,
      };

  return (
    <div
      ref={wrapperRef}
      style={{
        position: "absolute",
        ...maxStyle,
        zIndex: z,
        display: hidden ? "none" : "block",
      }}
      onMouseDown={() => bringToFront(id)}
    >
      <div className="card windows-box-shadow" style={{ height: maximized ? "100%" : "auto" }}>
        <div
          className="card-header d-flex align-items-center justify-content-between"
          style={{ cursor: maximized ? "default" : "move", userSelect: "none" }}
        >
          <div className="d-flex align-items-center gap-2">
            {iconSrc && <img src={iconSrc} alt="" style={{ width: 18, height: 18, marginRight: 6 }} />}
            <span className="my-0">{title}</span>
          </div>
          {/* Window controls */}
          <div className="d-flex align-items-center gap-1">
            <button
              aria-label="Minimize"
              title="Minimize"
              onClick={(e) => {
                e.stopPropagation();
                onMinimize(id);
              }}
              className="btn btn-sm"
              style={{ lineHeight: 1, padding: "0 8px" }}
            >
              â€”
            </button>
            <button
              aria-label={maximized ? "Restore" : "Maximize"}
              title={maximized ? "Restore" : "Maximize"}
              onClick={(e) => {
                e.stopPropagation();
                onMaximize(id);
              }}
              className="btn btn-sm"
              style={{ lineHeight: 1, padding: "0 8px" }}
            >
              {maximized ? "ðŸ——" : "â–¡"}
            </button>
            <button
              aria-label="Close"
              title="Close"
              onClick={(e) => {
                e.stopPropagation();
                onClose(id);
              }}
              className="btn btn-sm btn-danger"
              style={{ lineHeight: 1, padding: "0 8px" }}
            >
              âœ•
            </button>
          </div>
        </div>
        <div className="card-body" style={{ height: maximized ? "calc(100% - 56px)" : "auto", overflow: "auto" }}>
          {children}
        </div>
      </div>
    </div>
  );
}

function App() {
  const [activeTab, setActiveTab] = useState("skills");
  const [selectedIcon, setSelectedIcon] = useState(null);
  const appRef = useRef(null);

  // z-index manager
  const zCounter = useRef(50);
  const [zMap, setZMap] = useState({ about: 51, projects: 52, webamp: 53 });

  // windows state
  const [windows, setWindows] = useState({
    about: { id: "about", title: "About ME", open: true, minimized: false, maximized: false, icon: "/assets/icons/note-0.png", initial: { left: 40, top: 260, width: 840 } },
    projects: { id: "projects", title: "Projects", open: true, minimized: false, maximized: false, icon: "/assets/icons/computer_2_cool-0.png", initial: { left: 60, top: 620, width: 800 } },
    webamp: { id: "webamp", title: "Webamp ðŸŽ¶", open: true, minimized: false, maximized: false, icon: "/assets/icons/computer_2_cool-4.png", initial: { left: 520, top: 260, width: 720 } },
  });

  const bringToFront = (id) => {
    setZMap((prev) => ({ ...prev, [id]: ++zCounter.current }));
  };

  const setMinimized = (id, value) =>
    setWindows((prev) => ({ ...prev, [id]: { ...prev[id], minimized: value, open: value ? prev[id].open : true } }));

  const toggleMaximized = (id) =>
    setWindows((prev) => ({ ...prev, [id]: { ...prev[id], maximized: !prev[id].maximized, minimized: false } }));

  const closeWindow = (id) =>
    setWindows((prev) => ({ ...prev, [id]: { ...prev[id], open: false, minimized: false, maximized: false } }));

  const reopenWindow = (id) =>
    setWindows((prev) => ({ ...prev, [id]: { ...prev[id], open: true, minimized: false } }));

  // Start menu checkbox is handled by CSS; we just include the structure
  useEffect(() => {
    loadWin95Css();
  }, []);

  // Click outside icons clears selection
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (!appRef.current?.contains(e.target) || !e.target.closest(".desktop-icon")) {
        setSelectedIcon(null);
      }
    };
    document.addEventListener("click", handleClickOutside);
    return () => document.removeEventListener("click", handleClickOutside);
  }, []);

  // Desktop icons config
  const icons = [
    { id: "resume", label: "Resume", img: "/assets/icons/directory_open_file_mydocs-4.png", x: 450, y: 120, url: "/assets/devops_resume.pdf" },
    { id: "blog", label: "Blog", img: "/assets/icons/url1-0.png", x: 330, y: 120, url: "https://tech.brynnpark.xyz/" },
  ];

  const openIcon = (id) => {
    const target = icons.find((i) => i.id === id);
    if (target) window.open(target.url, "_blank");
  };

  // Time text for the taskbar (optional dynamic)
  const [timeText, setTimeText] = useState("");
  useEffect(() => {
    const tick = () => {
      const d = new Date();
      const hh = d.getHours();
      const mm = d.getMinutes().toString().padStart(2, "0");
      const ampm = hh >= 12 ? "PM" : "AM";
      const hh12 = ((hh + 11) % 12) + 1;
      setTimeText(`${hh12}:${mm} ${ampm}`);
    };
    tick();
    const i = setInterval(tick, 30000);
    return () => clearInterval(i);
  }, []);

  return (
    <div
      ref={appRef}
      className="App"
      style={{
        position: "relative",
        backgroundImage: "url('/assets/windowsxp.jpg')",
        backgroundSize: "cover",
        minHeight: "100vh",
        paddingBottom: "3.75rem", // room for start bar
        overflow: "hidden",
      }}
    >
      {/* Navbar (optional keep) */}
      <nav className="navbar navbar-expand-lg navbar-light navbar-95">
        <a className="navbar-brand" href="#">
          <img src="/assets/icons/computer-3.png" alt="icon" /> Navbar
        </a>
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarNavDropdown"
          aria-controls="navbarNavDropdown"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarNavDropdown">
          <ul className="navbar-nav">
            <li className="nav-item active">
              <a className="nav-link" href="#">About Me</a>
            </li>
            <li className="nav-item dropdown">
              <a className="nav-link dropdown-toggle" href="#" role="button" data-toggle="dropdown">Projects</a>
              <div className="dropdown-menu">
                <button className="dropdown-item" onClick={() => reopenWindow("projects")}>Open â€œProjectsâ€</button>
                <button className="dropdown-item" onClick={() => reopenWindow("about")}>Open â€œAboutâ€</button>
                <button className="dropdown-item" onClick={() => reopenWindow("webamp")}>Open â€œWebampâ€</button>
              </div>
            </li>
            <li className="nav-item"><a className="nav-link" href="#blog">Blog</a></li>
          </ul>
