import React, { useEffect } from "react";
import Draggable from "react-draggable";
import Webamp from "webamp";

export default function WebampWindow() {
  useEffect(() => {
    new Webamp({
      initialTracks: [
        {
          metaData: { artist: "Bohyeon", title: "Retro Vibes" },
          url: "https://cdn.jsdelivr.net/gh/captbaritone/webamp@master/mp3/hello.mp3",
          duration: 5.322,
        },
      ],
    }).renderWhenReady(document.getElementById("winamp-container"));
  }, []);

  return (
    <Draggable handle=".title-bar">
      <div className="window" style={{ position: "absolute", top: 80, left: 80, width: 340, zIndex: 10 }}>
        <div className="title-bar">
          <div className="title-bar-text">Winamp </div>
          <div className="title-bar-controls">
            <button aria-label="Minimize" />
            <button aria-label="Maximize" />
            <button aria-label="Close" />
          </div>
        </div>
        <div className="window-body" id="winamp-container" style={{ minHeight: 180 }}>
          <p>Loading Winamp...</p>
        </div>
      </div>
    </Draggable>
  );
}
