import React, { useEffect, useRef, useState } from "react";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import "98.css";


const loadWin95Css = () => {
  const link = document.createElement("link");
  link.rel = "stylesheet";
  link.href = process.env.PUBLIC_URL + "/assets/win95.css";
  document.head.appendChild(link);
};

/* Desktop icon */
function DesktopIcon({ id, label, img, x, y, selected, onSelect, onOpen, onMove }) {
  const ref = useRef(null);
  const dragging = useRef(false);
  const start = useRef({ x: 0, y: 0 });
  const origin = useRef({ x: 0, y: 0 });

  useEffect(() => {
    const down = (e) => {
      const p = e.touches?.[0] ?? e;
      dragging.current = true;
      start.current = { x: p.clientX, y: p.clientY };
      origin.current = { x, y };
      onSelect(id);
      document.body.style.userSelect = "none";
    };
    const move = (e) => {
      if (!dragging.current) return;
      const p = e.touches?.[0] ?? e;
      const nx = origin.current.x + (p.clientX - start.current.x);
      const ny = origin.current.y + (p.clientY - start.current.y);
      onMove(id, Math.max(8, nx), Math.max(80, ny)); // simple bounds
    };
    const up = () => {
      dragging.current = false;
      document.body.style.userSelect = "";
    };

    const el = ref.current;
    el.addEventListener("mousedown", down);
    window.addEventListener("mousemove", move);
    window.addEventListener("mouseup", up);
    el.addEventListener("touchstart", down, { passive: false });
    window.addEventListener("touchmove", move, { passive: false });
    window.addEventListener("touchend", up);

    return () => {
      el.removeEventListener("mousedown", down);
      window.removeEventListener("mousemove", move);
      window.removeEventListener("mouseup", up);
      el.removeEventListener("touchstart", down);
      window.removeEventListener("touchmove", move);
      window.removeEventListener("touchend", up);
    };
  }, [id, x, y, onSelect, onMove]);

  return (
    <div
      ref={ref}
      className="loaded-icon"
      style={{ position: "absolute", left: x, top: y, width: 75 }}
    >
      <div
        role="button"
        tabIndex={0}
        onDoubleClick={() => onOpen(id)}
        onKeyDown={(e) => {
          if (e.key === "Enter") onOpen(id);
          if (e.key === " ") onSelect(id);
        }}
        className={`desktop-icon ${selected ? "selected" : ""}`}
        style={{ textDecoration: "none", display: "block", textAlign: "center", cursor: "move" }}
      >
        <img src={img} alt={`${label} Icon`} style={{ width: 52, height: 52 }} />
        <div className="desktop-icon-title">{label}</div>
      </div>
    </div>
  );
}


/* Draggable window (drag by .card-header) + header controls */
function DraggableWindow({
  id,
  title,
  iconSrc,
  initial = { left: 60, top: 260, width: 640 },
  z,
  bringToFront,
  minimized,
  maximized,
  onMinimize,
  onMaximize,
  onClose,
  children,
}) {
  const wrapperRef = useRef(null);
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [startOffset, setStartOffset] = useState({ x: 0, y: 0 });
  const dragging = useRef(false);

  useEffect(() => {
    const root = wrapperRef.current;
    if (!root) return;
    const handle = root.querySelector(".card-header");
    if (!handle) return;

    const onDown = (e) => {
      if (minimized || maximized) return;
      bringToFront(id);
      dragging.current = true;
      const p = e.touches?.[0] ?? e;
      setDragStart({ x: p.clientX, y: p.clientY });
      setStartOffset({ ...offset });
      document.body.style.userSelect = "none";
    };
    const onMove = (e) => {
      if (!dragging.current) return;
      const p = e.touches?.[0] ?? e;
      const dx = p.clientX - dragStart.x;
      const dy = p.clientY - dragStart.y;
      setOffset({ x: startOffset.x + dx, y: startOffset.y + dy });
    };
    const onUp = () => {
      dragging.current = false;
      document.body.style.userSelect = "";
    };

    const opts = { passive: false };
    handle.addEventListener("mousedown", onDown, opts);
    window.addEventListener("mousemove", onMove, opts);
    window.addEventListener("mouseup", onUp, opts);
    handle.addEventListener("touchstart", onDown, opts);
    window.addEventListener("touchmove", onMove, opts);
    window.addEventListener("touchend", onUp, opts);

    return () => {
      handle.removeEventListener("mousedown", onDown, opts);
      window.removeEventListener("mousemove", onMove, opts);
      window.removeEventListener("mouseup", onUp, opts);
      handle.removeEventListener("touchstart", onDown, opts);
      window.removeEventListener("touchmove", onMove, opts);
      window.removeEventListener("touchend", onUp, opts);
      document.body.style.userSelect = "";
    };
  }, [offset, dragStart, startOffset, minimized, maximized, id, bringToFront]);

  const hidden = minimized;

  const frameStyle = maximized
    ? { left: 0, top: 0, width: "100%", height: "calc(100vh - 56px)", transform: "translate(0,0)" }
    : { left: initial.left, top: initial.top, width: initial.width || "min(1100px, 96vw)", transform: `translate(${offset.x}px, ${offset.y}px)` };

  return (
    <div
      ref={wrapperRef}
      style={{ position: "absolute", ...frameStyle, zIndex: z, display: hidden ? "none" : "block" }}
      onMouseDown={() => bringToFront(id)}
    >
      <div className="card" style={{ height: maximized ? "100%" : "auto" }}>
        <div className="card-header d-flex align-items-center justify-content-between" style={{ cursor: maximized ? "default" : "move", userSelect: "none" }}>
          <div className="d-flex align-items-center">
            {iconSrc && <img src={iconSrc} alt="" style={{ width: 18, height: 18, marginRight: 6 }} />}
            <span className="my-0">{title}</span>
          </div>
          <div className="d-flex align-items-center">
            <button className="btn btn-sm" style={{ height: 22, width: 28, marginRight: 6, border: "1px outset #fff", padding: 0, lineHeight: "18px" }}
              title="Minimize" onClick={(e) => { e.stopPropagation(); onMinimize(id); }}>â€”</button>
            <button className="btn btn-sm" style={{ height: 22, width: 28, marginRight: 6, border: "1px outset #fff", padding: 0, lineHeight: "18px" }}
              title={maximized ? "Restore" : "Maximize"} onClick={(e) => { e.stopPropagation(); onMaximize(id); }}>
              {maximized ? "ðŸ——" : "â–¡"}
            </button>
            <button className="btn btn-sm btn-danger" style={{ height: 22, width: 28, padding: 0, lineHeight: "18px" }}
              title="Close" onClick={(e) => { e.stopPropagation(); onClose(id); }}>âœ•</button>
          </div>
        </div>
        <div className="card-body" style={{ height: maximized ? "calc(100% - 56px)" : "auto", overflow: "auto" }}>
          {children}
        </div>
      </div>
    </div>
  );
}

export default function App() {
  const [activeTab, setActiveTab] = useState("skills");
  const [selectedIcon, setSelectedIcon] = useState(null);
  const appRef = useRef(null);

  // z-order
