import React, { useEffect, useRef, useState } from "react";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";

const loadWin95Css = () => {
  const link = document.createElement("link");
  link.rel = "stylesheet";
  link.href = process.env.PUBLIC_URL + "/assets/win95.css";
  document.head.appendChild(link);
};

// Reusable desktop icon
function DesktopIcon({ id, label, img, x, y, selected, onSelect, onOpen }) {
  return (
    <div className="loaded-icon" style={{ position: "absolute", right: x, top: y, width: 75 }}>
      <div
        role="button"
        tabIndex={0}
        onClick={() => onSelect(id)}
        onDoubleClick={() => onOpen(id)}
        onKeyDown={(e) => {
          if (e.key === "Enter") onOpen(id);
          if (e.key === " ") onSelect(id);
        }}
        className={`desktop-icon ${selected ? "selected" : ""}`}
        style={{ textDecoration: "none", display: "block", textAlign: "center", cursor: "pointer" }}
      >
        <img src={img} alt={`${label} Icon`} style={{ width: 52, height: 52 }} />
        <div className="desktop-icon-title">{label}</div>
      </div>
    </div>
  );
}

// Draggable wrapper (drag by the card header)
function DraggableCard({
  initial = { left: 40, top: 260 },
  children,
  handleSelector = ".card-header",
  getNextZIndex,
}) {
  const wrapperRef = useRef(null);
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const start = useRef({ x: 0, y: 0 });
  const startOffset = useRef({ x: 0, y: 0 });
  const dragging = useRef(false);
  const [z, setZ] = useState(1);

  useEffect(() => {
    const root = wrapperRef.current;
    if (!root) return;
    const handle = root.querySelector(handleSelector);
    if (!handle) return;

    const onDown = (e) => {
      dragging.current = true;
      const p = e.touches?.[0] ?? e;
      start.current = { x: p.clientX, y: p.clientY };
      startOffset.current = { ...offset };

      // bring to front
      if (getNextZIndex) setZ(getNextZIndex());

      document.body.style.userSelect = "none";
    };

    const onMove = (e) => {
      if (!dragging.current) return;
      const p = e.touches?.[0] ?? e;
      const dx = p.clientX - start.current.x;
      const dy = p.clientY - start.current.y;
      setOffset({ x: startOffset.current.x + dx, y: startOffset.current.y + dy });
    };

    const onUp = () => {
      dragging.current = false;
      document.body.style.userSelect = "";
    };

    // Pointer + mouse + touch
    const opts = { passive: false };
    handle.addEventListener("pointerdown", onDown, opts);
    window.addEventListener("pointermove", onMove, opts);
    window.addEventListener("pointerup", onUp, opts);

    handle.addEventListener("mousedown", onDown, opts);
    window.addEventListener("mousemove", onMove, opts);
    window.addEventListener("mouseup", onUp, opts);

    handle.addEventListener("touchstart", onDown, opts);
    window.addEventListener("touchmove", onMove, opts);
    window.addEventListener("touchend", onUp, opts);

    return () => {
      handle.removeEventListener("pointerdown", onDown, opts);
      window.removeEventListener("pointermove", onMove, opts);
      window.removeEventListener("pointerup", onUp, opts);

      handle.removeEventListener("mousedown", onDown, opts);
      window.removeEventListener("mousemove", onMove, opts);
      window.removeEventListener("mouseup", onUp, opts);

      handle.removeEventListener("touchstart", onDown, opts);
      window.removeEventListener("touchmove", onMove, opts);
      window.removeEventListener("touchend", onUp, opts);

      document.body.style.userSelect = "";
    };
  }, [offset, handleSelector, getNextZIndex]);

  return (
    <div
      ref={wrapperRef}
      style={{
        position: "absolute",
        left: initial.left,
        top: initial.top,
        transform: `translate(${offset.x}px, ${offset.y}px)`,
        width: "min(1200px, 96vw)",
        zIndex: z,
      }}
    >
      {children}
    </div>
  );
}

function App() {
  const [isProjectsOpen, setProjectsOpen] = useState(false);
  const [isMoreOpen, setMoreOpen] = useState(false);
  const [activeTab, setActiveTab] = useState("skills");
  const [selectedIcon, setSelectedIcon] = useState(null);
  const appRef = useRef(null);

  // simple z-index manager for draggable windows
  const zCounter = useRef(10);
  const getNextZIndex = () => ++zCounter.current;

  useEffect(() => {
    loadWin95Css();
  }, []);

  // Click outside to clear icon selection
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (!appRef.current?.contains(e.target) || !e.target.closest(".desktop-icon")) {
        setSelectedIcon(null);
      }
    };
    document.addEventListener("click", handleClickOutside);
    return () => document.removeEventListener("click", handleClickOutside);
  }, []);

  const toggleProjects = (e) => {
    e.preventDefault();
    setProjectsOpen((prev) => !prev);
    setMoreOpen(false);
  };

  const toggleMore = (e) => {
    e.preventDefault();
    setMoreOpen((prev) => !prev);
    setProjectsOpen(false);
  };

  // Desktop icons
  const icons = [
    { id: "resume", label: "Resume", img: "/assets/icons/directory_open_file_mydocs-4.png", x: 450, y: 120, url: "/assets/devops_resume.pdf" },
    { id: "blog", label: "Blog", img: "/assets/icons/url1-0.png", x: 330, y: 120, url: "https://tech.brynnpark.xyz/" },
  ];

  const openIcon = (id) => {
    const target = icons.find((i) => i.id === id);
    if (target) window.open(target.url, "_blank");
  };

  return (
    <div
      ref={appRef}
      className="App"
      style={{
        position: "relative", // important for absolute children
        backgroundImage: "url('/assets/windowsxp.jpg')",
        backgroundSize: "cover",
        minHeight: "100vh",
        overflow: "hidden",
      }}
    >
      {/* Navbar (fixed) */}
      <nav className="navbar navbar-expand-lg navbar-light navbar-95">
        <a className="navbar-brand" href="#">
          <img src="/assets/icons/computer-3.png" alt="icon" /> Navbar
        </a>
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarNavDropdown"
          aria-controls="navbarNavDropdown"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarNavDropdown">
          <ul className="navbar-nav">
            <li className="nav-item active">
              <a className="nav-link" href="#">About Me</a>
            </li>

            {/* Projects Dropdown */}
            <li className={`nav-item dropdown ${isProjectsOpen ? "show" : ""}`}>
              <a
                className="nav-link dropdown-toggle"
                href="#"
                id="projectsDropdown"
                role="button"
                onClick={toggleProjects}
                aria-haspopup="true"
                aria-expanded={isProjectsOpen}
              >
                Projects
              </a>
              <div className={`dropdown-menu ${isProjectsOpen ? "show" : ""}`} aria-labelledby="projectsDropdown">
                <a className="dropdown-item" href="#">MSA project (DevOps)</a>
                <a className="dropdown-item" href="#">DB Build</a>
                <a className="dropdown-item" href="#">Blockchain</a>
              </div>
            </li>

            {/* Blog */}
            <li className="nav-item">
              <a className="nav-link" href="#blog">Blog</a>
            </li>

            {/* More Dropdown */}
            <li className={`nav-item dropdown ${isMoreOpen ? "show" : ""}`}>
              <a
                className="nav-link dropdown-toggle"
                href="#"
                id="moreDropdown"
                role="button"
                onClick={toggleMore}
                aria-haspopup="true"
                aria-expanded={isMoreOpen}
              >
                More
              </a>
              <div className={`dropdown-menu ${isMoreOpen ? "show" : ""}`} aria-labelledby="moreDropdown">
                <a className="dropdown-item" href="#">Education</a>
                <a className="dropdown-item" href="#">Certificate</a>
                <a className="dropdown-item" href="#">Experience</a>
              </div>
            </li>
          </ul>
        </div>

        <div className="col-0">
          <a href="mailto:brynnparkjustdoit@email.com" className="btn start-button">
            <img src="/assets/icons/users-1.png" alt="icon" className="icon-16" /> Contact Me
          </a>
          <a href="https://github.com/brynnPark/" className="btn start-button">
            <img src="/assets/icons/directory_folder_options-5.png" alt="icon" className="icon-16" /> GitHub
          </a>
        </div>
      </nav>

      {/* Header text (fixed) */}
      <div className="header mt-5 mb-2 text-center">
        <p className="lead" style={{ color: "black" }}>
          Cloud-native engineer with a retro soul üíñ
        </p>
      </div>

      {/* Desktop Icons (fixed) */}
      {icons.map((icon) => (
        <DesktopIcon
          key={icon.id}
          id={icon.id}
          label={icon.label}
          img={icon.img}
          x={icon.x}
          y={icon.y}
          selected={selectedIcon === icon.id}
          onSelect={setSelectedIcon}
          onOpen={openIcon}
        />
      ))}

      {/* ABOUT (drag by header) */}
      <DraggableCard initial={{ left: 40, top: 260 }} getNextZIndex={getNextZIndex}>
        <div className="container">
          <div className="row mb-5">
            <div className="col-md-7 my-auto">
              <div className="card">
                <div className="card-header">
                  <h4 className="my-1 font-weight-normal">
                    <img src="/assets/icons/note-0.png" alt="icon" /> About ME
                  </h4>
                </div>
                <div className="card-body">
                  <ul className="list-unstyled mt-3 mb-4">
                    <li>üîß Cloud & Infrastructure Engineer</li>
                    <li>üé® Lover of retro, pink, and aesthetics</li>
                    <li>üìö Exploring Linux internals, NUMA, and beyond</li>
                    <li>üí° I enjoy blogging, mentoring, and building things</li>
                  </ul>
                  <a href="personal_page.html" className="btn btn-lg btn-block"> Contact me </a>
                </div>
              </div>
            </div>

            <div className="col-md-5 my-auto">
              <div className="container mb-4">
                <ul className="nav nav-tabs">
                  {["skills", "profile", "contact"].map((tab) => (
                    <li className="nav-item" key={tab}>
                      <button
                        className={`nav-link ${activeTab === tab ? "active" : ""}`}
                        onClick={() => setActiveTab(tab)}
                      >
                        {tab.charAt(0).toUpperCase() + tab.slice(1)}
                      </button>
                    </li>
                  ))}
                </ul>
                <div className="tab-content" style={{ minHeight: "210px" }}>
                  {activeTab === "skills" && <div>üè† Home tab content</div>}
                  {activeTab === "profile" && <div>üë§ Profile tab content</div>}
                  {activeTab === "contact" && <div>‚úâÔ∏è Contact tab content</div>}
                </div>
              </div>
            </div>
          </div>
        </div>
      </DraggableCard>

      {/* PROJECTS (drag by header) */}
      <DraggableCard initial={{ left: 60, top: 620 }} getNextZIndex={getNextZIndex}>
        <div className="container">
          <div className="card mb-4">
            <div className="card-header icon">
              <h4 className="my-2 font-weight-normal">
                <img src="/assets/icons/computer_2_cool-0.png" alt="icon" /> Projects
              </h4>
            </div>
            <div className="card-body">
              <ul className="list-unstyled">
                <li><strong>üîß Home Server GitOps</strong> ‚Äî RPi5 + ArgoCD + Cert renewal automation</li>
                <li><strong>üêß WSS Kernel Module</strong> ‚Äî NUMA hinting fault analysis with AET/MRC</li>
                <li><strong>ü™Ñ ITeamoa</strong> ‚Äî Serverless microservices architecture (AWS)</li>
              </ul>
            </div>
          </div>
        </div>
      </DraggableCard>

      {/* WEBAMP (drag by header) */}
      <DraggableCard initial={{ left: 520, top: 260 }} getNextZIndex={getNextZIndex}>
        <div className="container mb-4 text-center">
          <div className="card">
            <div className="card-header">
              <h4>
                <img src="/assets/icons/computer_2_cool-4.png" alt="icon" /> Webamp is coming soon üé∂
              </h4>
            </div>
            <div className="card-body">
              <div id="app" style={{ minHeight: 200 }} />
              <p className="mt-2">* Audio player not available yet. Waiting for your MP3s :)</p>
            </div>
          </div>
        </div>
      </DraggableCard>

      {/* Footer (fixed taskbar) */}
      <footer className="taskbar mt-5">
        <div className="row" style={{ marginRight: 0 }}>
          <div className="col-0 time text-right"> üíæ brynnpark copyright </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
